      this.$router.push('/simulations');
    },
    // 状态提示显示方法
    showInfoStatus: function showInfoStatus(message) {
      this.connectionStatusText = message;
      this.connectionStatusColor = 'info';
      this.showConnectionStatus = true;
    },
    showSuccessStatus: function showSuccessStatus(message) {
      this.connectionStatusText = message;
      this.connectionStatusColor = 'success';
      this.showConnectionStatus = true;
    },
    showWarningStatus: function showWarningStatus(message) {
      this.connectionStatusText = message;
      this.connectionStatusColor = 'warning';
      this.showConnectionStatus = true;
    },
    showErrorStatus: function showErrorStatus(message) {
      this.connectionStatusText = message;
      this.connectionStatusColor = 'error';
      this.showConnectionStatus = true;
    },
    // 错误处理
    handleApiError: function handleApiError(error) {
      console.error('API错误:', error);

      // 特定错误处理
      if (error.status === 404 && error.message.includes('Simulation not found')) {
        this.$toast.error('模拟不存在或已被删除');
        this.$router.push('/simulations');
      }
    },
    // 页面可见性处理
    handleVisibilityChange: function handleVisibilityChange() {
      if (document.hidden) {
        // 页面隐藏时暂停WebSocket
        this.stopHeartbeat();
      } else {
        // 页面可见时恢复WebSocket
        if (this.socket && this.socket.readyState === WebSocket.OPEN) {
          this.startHeartbeat();
        } else if (this.isRunning && !this.isCaptured) {
          // 如果模拟正在运行，但WebSocket关闭，则重新连接
          this.setupWebSocket();
        }
      }
    },
    // 窗口焦点变化处理
    handleWindowFocus: function handleWindowFocus() {
      if (this.isRunning && !this.isCaptured) {
        // 如果模拟正在运行，确保WebSocket连接正常
        if (!this.socket || this.socket.readyState !== WebSocket.OPEN) {
          this.setupWebSocket();
        }
      }
    },
    handleWindowBlur: function handleWindowBlur() {
      // 窗口失去焦点时的处理（如果需要）
      // 可以降低更新频率等
      this.optimizeChartUpdates = true;
    },
    // 清理定时器
    cleanupTimers: function cleanupTimers() {
      if (this.dataUpdateTimer) {
        clearTimeout(this.dataUpdateTimer);
        this.dataUpdateTimer = null;
      }
      if (this.chartUpdateTimer) {
        clearTimeout(this.chartUpdateTimer);
        this.chartUpdateTimer = null;
      }
    }
  },
  watch: {
    simulationId: {
      handler: function handler(newId, oldId) {
        if (newId !== oldId) {
          // 重新初始化组件
          this.closeWebSocket();
          this.cleanupTimers();
          this.initializeComponent();
        }
      },
      immediate: false
    },
    // 监视store中的当前模拟数据变化
    storeCurrentSimulation: {
      handler: function handler(newSimulation) {
        if (newSimulation && newSimulation.id === this.simulationId) {
          // 仅在首次加载或重要属性变化时更新本地数据
          if (!this.simulation.id || newSimulation.is_running !== this.simulation.is_running || newSimulation.is_captured !== this.simulation.is_captured) {
            this.simulation = newSimulation;
            this.isRunning = newSimulation.is_running;
            this.isCaptured = newSimulation.is_captured;
          }
        }
      },
      deep: true
    }
  }
};
